mapper.update:
update qnacoment

	qnaco_content = #{qnaco_content}, qnaco_moddate=now()
	, ad_no = #{ad_no} 



		qnaco_no = #{qnaco_no} and qna_no = #{qna_no} and ui_no = #{ui_no}


		qnaco_no = #{qnaco_no} and qna_no = #{qna_no}

mapper.delete:
delete from qnacoment


		qnaco_no = #{qnaco_no} and qna_no = #{qna_no} and ui_no = #{ui_no}


		qnaco_no = #{qnaco_no}
