mapper.update:
update user_info

	ui_name = #{ui_name}
	, ui_pwd = #{ui_pwd}
	, ui_nick = #{ui_nick}
	, ui_support = #{ui_support}
	, ui_email = #{ui_email}
	, ui_tell = #{ui_tell}
	, ui_active = #{ui_active}

where ui_no = #{ui_no}

mapper.select:
select ui_id, ui_name, ui_nick, ui_support, ui_email, ui_tell from user_info

	ui_name like CONCAT('%', #{ui_name}, '%')
	and ui_nick like CONCAT ('%', #{ui_nick}, '%')
	and ui_support = #{ui_support} 

	order by ui_no desc LIMIT #{paging.startViewNum}, #{paging.viewLength}
